		var result = [];
		for (var i = 0; i < nodes.length; i++) {
			var url = new Query('D:href').findFirst(nodes[i]).textContent;
			if (!/!svn/.test(url)) // ignore svn dirs
				result.push(new WebResource(this.getURL().withPath(url)))
		}
