  growFromNothingAt: function(p, callWhenDone) {
    var desiredScale = 1;
    this.setScale(desiredScale * 0.01);
    this.setPosition(p);
    this.stayCenteredAndSmoothlyScaleTo(desiredScale, p, callWhenDone);
  }

